import '../styles/tour-details.css'
import React, {useRef, useState} from 'react'
import { Container,Row,Col,Form,ListGroup } from 'reactstrap'
import { useParams } from 'react-router-dom'
import tourData from '../assets/data/tours'
import calculateAvgRating from '../utils/avgRating'
import avatar from "../assets/images/avatar.jpg"
import Booking from '../components/Booking/Booking'
import Newsletter from '../shared/Newsletter'
import useFetch from './../hooks/useFetch'
import { BASE_URL} from './../utils/config'
const TourDetails = () => {
  const {id }= useParams()
  const reviewMsgRef = useRef();
  const [tourRating, setTourRating] = useState(null);

  
    // Fetch data from the database
    const { data: tour, error } = useFetch(`${BASE_URL}/tours/${id}`);
  
    // Handle loading and error states
    if (error) {
      return <div>Error: {error}</div>;
    }
    if (!tour) {
      return <div>Loading...</div>;
    }
  
    const {
      photo,
      title,
      desc,
      price,
      reviews = [],
      address,
      city,
      distance,
      maxGroupSize,
    } = tour;
  
    const { totalRating, avgRating } = calculateAvgRating(reviews);
  
    const options = { day: "numeric", month: "long", year: "numeric" };
  
    const submitHandler = async (e) => {
      e.preventDefault();
      const reviewText = reviewMsgRef.current.value;
  
      const reviewData = {
        text: reviewText,
        rating: tourRating,
      };
  
      try {
        const response = await fetch(`${BASE_URL}/tours/${id}/reviews`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(reviewData),
        });
  
        const result = await response.json();
        if (response.ok) {
          alert('Review submitted successfully!');
          // Optionally, refresh the page or fetch updated data
        } else {
          alert(result.message || 'Failed to submit review.');
        }
      } catch (error) {
        console.error(error);
        alert('An error occurred while submitting the review.');
      }
    };
  
    return (
      <>
        <section>
          <Container>
            <Row>
              <Col lg="8">
                <div className="tour_content">
                  <img src={photo} alt="" />
                  <div className="tour_info">
                    <h2>{title}</h2>
                    <div className="d-flex align-items-center gap-5">
                      <span className="tour_rating d-flex align-items-center gap-1">
                        <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                        {avgRating === 0 ? null : avgRating}
                        {totalRating === 0 ? (
                          "Not rated"
                        ) : (
                          <span>({reviews.length})</span>
                        )}
                      </span>
                      <span>
                        <i className="ri-map-pin-fill"></i>
                        {address}
                      </span>
                    </div>
                    <div className="tour_extra-details">
                      <span>
                        <i className="ri-map-pin-2-line"></i> {city}
                      </span>
                      <span>
                        <i className="ri-money-dollar-circle-line"></i> ${price} /per person
                      </span>
                      <span>
                        <i className="ri-map-pin-time-line"></i> {distance} k/m
                      </span>
                      <span>
                        <i className="ri-group-line"></i> {maxGroupSize} people
                      </span>
                    </div>
  
                    <h5>Description</h5>
                    <p>{desc}</p>
                  </div>
  
                  {/* Tour review section */}
                  <div className="tour_reviews mt-4">
                    <h4>Reviews ({reviews.length} reviews)</h4>
                    <Form onSubmit={submitHandler}>
                      <div className="d-flex align-items-center gap-3 mb-4 rating_group">
                        {[1, 2, 3, 4, 5].map((star) => (
                          <span key={star} onClick={() => setTourRating(star)}>
                            {star} <i className="ri-star-s-fill"></i>
                          </span>
                        ))}
                      </div>
                      <div className="review_input">
                        <input
                          type="text"
                          ref={reviewMsgRef}
                          placeholder="Share your thoughts"
                          required
                        />
                        <button className="primary_btn text-white" type="submit">
                          Submit
                        </button>
                      </div>
                    </Form>
                    <ListGroup className="user_reviews">
                      {reviews.map((review, index) => (
                        <div className="review_item" key={index}>
                          <img src={avatar} alt="" />
                          <div className="w-100">
                            <div className="d-flex align-items-center justify-content-between">
                              <div>
                                <h5>{review.username || "Anonymous"}</h5>
                                <p>
                                  {new Date(review.createdAt).toLocaleDateString(
                                    "en-US",
                                    options
                                  )}
                                </p>
                              </div>
                              <span className="d-flex align-items-center">
                                {review.rating}
                                <i className="ri-star-s-fill"></i>
                              </span>
                            </div>
                            <h6>{review.comment}</h6>
                          </div>
                        </div>
                      ))}
                    </ListGroup>
                  </div>
                  {/* End of Tour review section */}
                </div>
              </Col>
              <Col lg="4">
                <Booking tour={tour} avgRating={avgRating} />
              </Col>
            </Row>
          </Container>
        </section>
        <Newsletter />
      </>
    );
  };
  
  export default TourDetails;
