import React from 'react'
import {Container,Row,Button} from 'reactstrap'//importing reactstrap components
import { NavLink,Link} from 'react-router-dom'
import logo from '../../assets/images/logo.png'

const nav__links  = [

{
path: '/home',
display: 'Home'

},
{
  path: '#' ,
  display: 'About'
},
{
  path: '/tours',
  display: 'Tours'
},

]

const Header = () => {
  return <header className='header'>
  <Container>
  <Row>
    <div className="nav__wrapper d-flex align-items-center 
    justify-content-between"></div>
    {/* ========== logo ============ */}
     <div className="logo">
      <img src={logo} alt="" />
     </div>
    {/* ========== logo end ============ */}

    {/* ========== menu start ============ */}
      <div className="navigation">
          <ul className="menu d-flex align-items-center gap-5">
          {
            nav__links.map((link,index)=>(
              <li className="nav__item" key={index}>
              <NavLink to={item.path}>{item.display}</NavLink>
              </li>
            ))
          }
          </ul>
      </div>
    {/* ========== menu end ============ */}
          <div className="nav__right d-flex align-item-center">

          </div>
  </Row>
  </Container>
  </header>
}

export default Header
