import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Table } from 'reactstrap';
import { BASE_URL } from '../config/config'; // Import BASE_URL
import '../styles/adminpanel.css';

const AdminDashboard = () => {
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all tours
  const fetchTours = async () => {
    try {
      const response = await fetch(`${BASE_URL}/tours`); // Use BASE_URL
      const data = await response.json();
      setTours(data.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching tours:', error);
      setLoading(false);
    }
  };

  // Delete tour
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchTours();
      } else {
        console.error('Failed to delete tour');
      }
    } catch (error) {
      console.error('Error deleting tour:', error);
    }
  };

  // Update tour (can be expanded)
  const handleUpdate = async (id, updatedData) => {
    try {
      const response = await fetch(`${BASE_URL}/tours/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      if (response.ok) {
        fetchTours();
      } else {
        console.error('Failed to update tour');
      }
    } catch (error) {
      console.error('Error updating tour:', error);
    }
  };

  useEffect(() => {
    fetchTours();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <Container>
      <h1 className="text-center my-4">Admin Dashboard</h1>
      <h2 className="text-center mb-4">Manage Tours</h2>

      <Row>
        <Col>
          <Table striped>
            <thead>
              <tr>
                <th>Tour Name</th>
                <th>Description</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {tours.map((tour) => (
                <tr key={tour._id}>
                  <td>{tour.name}</td>
                  <td>{tour.description}</td>
                  <td>{tour.price}</td>
                  <td>
                    <Button
                      color="danger"
                      onClick={() => handleDelete(tour._id)}
                      className="mr-2"
                    >
                      Delete
                    </Button>
                    <Button
                      color="warning"
                      onClick={() =>
                        handleUpdate(tour._id, { name: 'Updated Tour' })
                      }
                    >
                      Update
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
};

export default AdminDashboard;
