import React from 'react'
import {Container,Row,Button} from 'reactstrap'//importing reactstrap components
import { NavLink,Link} from 'react-router-dom'
import logo from '../../assets/images/logo.png'


const nav__links  = [

{
path: '/home',
display: 'Home'

},
{
  path: '#' ,
  display: 'About'
},
{
  path: '/tours',
  display: 'Tours'
},

]

const Header = () => {
  return <header className='header'>
  <Container>
  <Row>
    <div className="nav__wrapper d-flex align-items-center 
    justify-content-between"></div>
    {/* ========== logo ============ */}
     <div className="logo">
      <img src={logo} alt="" />
     </div>
    {/* ========== logo end ============ */}

    {/* ========== menu start ============ */}
      <div className="navigation">
          <ul className="menu d-flex align-items-center gap-5">
          {
            nav__links.map((item,index)=>(
              <li className="nav__item" key={index}>
              <NavLink to={item.path}>{item.display}</NavLink>
              </li>
            ))
          }
          </ul>
      </div>
    {/* ========== menu end ============ */}
          <div className="nav__right d-flex align-item-center gap-4">
             <div className="nav__btns d-flex align-item-center gap-4">
              <Button className="btn secondary__btn" ><link to='/login' />Login</Button>

              <Button className="btn primary__btn" ><link to='/login' />R</Button>
             </div>
          </div>
  </Row>
  </Container>
  </header>
}

export default Header
