import React, { useEffect, useState } from 'react'
import { BASE_URL } from '../utils/config'
import { useNavigate } from 'react-router-dom'

const AdminPanel = () => {
  const navigate = useNavigate()
  const [users, setUsers] = useState([])
  const [tours, setTours] = useState([])

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersRes = await fetch(`${BASE_URL}/admin/users`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('mySecretKeyForJWT')}`
          }
        })
        const usersData = await usersRes.json()

        const toursRes = await fetch(`${BASE_URL}/admin/tours`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('mySecretKeyForJWT')}`
          }
        })
        const toursData = await toursRes.json()

        setUsers(usersData)
        setTours(toursData)
      } catch (err) {
        console.log('Error fetching data', err)
        navigate('/login') // Redirect to login if there is an error
      }
    }

    fetchData()
  }, [])

  const deleteTour = async (id) => {
    try {
      await fetch(`${BASE_URL}/admin/tours/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('mySecretKeyForJWT')}`
        }
      })
      setTours(tours.filter((tour) => tour._id !== id))
    } catch (err) {
      console.log('Error deleting tour', err)
    }
  }

  return (
    <div>
      <h1>Admin Panel</h1>

      <h2>Users</h2>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.name} - {user.email} - {user.role}
          </li>
        ))}
      </ul>

      <h2>Tours</h2>
      <ul>
        {tours.map((tour) => (
          <li key={tour._id}>
            {tour.title} - {tour.city} - {tour.price}
            <button onClick={() => deleteTour(tour._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default AdminPanel
