import { useParams } from 'react-router-dom';
import tours from '../data/tours'; // assuming your data is imported from this file

const TourDetails = () => {
  const { id } = useParams();
  const tour = tours.find((tour) => tour.id === id); // Find the tour by id

  if (!tour) {
    return <div>Tour not found</div>;
  }

  return (
    <div>
      <h1>{tour.title}</h1>
      <img src={tour.photo} alt={tour.title} />
      <p>{tour.desc}</p>
      {/* Render other tour details here */}
    </div>
  );
};

export default TourDetails;
